query.sql:
SELECT "CLASS", "COUNT", "TIME" FROM(
select * from v$waitstat
)

SELECT "MB USED", "MB FREE" FROM(
select USED_BLOCKS*8192/1024/1024 "MB USED" , FREE_BLOCKS*8192/1024/1024 "MB FREE" from v$sort_segment where TABLESPACE_NAME='TEMP'
)

SELECT "USERNAME", "USER", "TABLESPACE", "Blocks USed", "SQL_ID", "SESSION_ADDR", "SQLADDR" FROM(
select username,user,tablespace,blocks*8192/1024/1024 "Blocks USed",sql_id,session_addr,sqladdr from V$TEMPSEG_USAGE
)

SELECT "EQ_NAME", "EQ_TYPE", "REQ_REASON", "TOTAL_REQ#", "TOTAL_WAIT#", "SUCC_REQ#", "FAILED_REQ#", "CUM_WAIT_TIME", "REQ_DESCRIPTION", "EVENT#" FROM(
select * from (
SELECT "EQ_NAME", "EQ_TYPE", "REQ_REASON", "TOTAL_REQ#", "TOTAL_WAIT#", "SUCC_REQ#", "FAILED_REQ#", "CUM_WAIT_TIME", "REQ_DESCRIPTION", "EVENT#" FROM(
select * from (
select * from  v$enqueue_statistics
 ) sub1 order by 8 desc
)
 ) sub1 order by 8 desc
)

SELECT "EQ_NAME", "EQ_TYPE", "REQ_REASON", "TOTAL_REQ#", "TOTAL_WAIT#", "SUCC_REQ#", "FAILED_REQ#", "CUM_WAIT_TIME", "REQ_DESCRIPTION", "EVENT#", "CON_ID" FROM(
select * from (
select * from  v$enqueue_statistics
 ) sub1 order by 8 desc
)

SELECT "ACTION_TIME", "ACTION", "NAMESPACE", "VERSION", "ID", "COMMENTS", "BUNDLE_SERIES" FROM(
select * from sys.registry$history
)

SELECT "FILE_NO", "FILETYPE_ID", "FILETYPE_NAME", "SMALL_READ_MEGABYTES", "SMALL_WRITE_MEGABYTES", "LARGE_READ_MEGABYTES", "LARGE_WRITE_MEGABYTES", "SMALL_READ_REQS", "SMALL_WRITE_REQS", "SMALL_SYNC_READ_REQS", "LARGE_READ_REQS", "LARGE_WRITE_REQS", "SMALL_READ_SERVICETIME", "SMALL_WRITE_SERVICETIME", "SMALL_SYNC_READ_LATENCY", "LARGE_READ_SERVICETIME", "LARGE_WRITE_SERVICETIME", "ASYNCH_IO", "ACCESS_METHOD", "RETRIES_ON_ERROR" FROM(
select * from V$IOSTAT_FILE
)

SELECT "STAT_NAME", "VALUE", "OSSTAT_ID", "COMMENTS", "CUMULATIVE", "CON_ID" FROM(
select * from v$OSSTAT
)

SELECT "PROTECTION_MODE" FROM(
SELECT PROTECTION_MODE FROM V$DATABASE
)

SELECT "NAME", "VALUE", "UNIT" FROM(
SELECT *  FROM V$PGASTAT
)

SELECT "INSTANCE_NAME", "BLOCKSIZE", "NAME", "VALUE" FROM(
select a.instance_name , b.blocksize , c.name, c.value FROM  v$instance a,  v$log b , v$sysstat c where c.name = 'redo wastage'
)

SELECT "GROUP#", "GB" FROM(
select group#, bytes/1024/1024/1024 GB from v$log
)

SELECT "GROUP#", "GB" FROM(
select group#, bytes/1024/1024/1024 GB from v$log
)

SELECT "GROUP#", "GB" FROM(
select group#, bytes/1024/1024/1024 GB from v$log
)

SELECT "OWNER", "SEGMENT_NAME", "SEGMENT_TYPE", "GIGABYTES" FROM(
select * from
(select owner,segment_name||'~'||partition_name segment_name,segment_type, round(bytes/(1024*1024*1024)) gigaBytes
from dba_segments where owner = upper( :owner )
ORDER BY BLOCKS desc) where rownum < 11
)

SELECT "TABLESPACE_NAME", "GB", "USED" FROM(
select * from (
select tablespace_name, floor( (tablespace_size*8192)/1024/1024/1024 ) GB , floor(used_percent)used from dba_tablespace_usage_metrics
 ) sub1 order by 3 asc
)

CREATE global temporary table TMP_TTZZ ( 
   tzone varchar2(100),
   stamp varchar2(100)
) ON COMMIT DELETE ROWS;

insert into TMP_TTZZ ( TZONE, STAMP )
SELECT  DBTIMEZONE
      ,TO_CHAR(SYSTIMESTAMP, 'YYYY-MM-DD HH24:MI:SS' )
  FROM DUAL;

  insert into TMP_TTZZ ( TZONE, STAMP )
  SELECT  SESSIONTIMEZONE
      ,TO_CHAR(LOCALTIMESTAMP, 'YYYY-MM-DD HH24:MI:SS' )
  FROM DUAL;

SELECT "GROUP#", "GB" FROM(
select group#, bytes/1024/1024/1024 GB from v$log
)

SELECT "JOB_NAME" FROM(
select job_name from dba_scheduler_jobs where job_name like '%STATS%'
)

SELECT "NBR_UNITS", "DAY" FROM(
select sum(total_nbr_of_units) NBR_UNITS ,to_char(proc_dttm , 'MONTH DD,YYYY') DAY From pwm350.outpt_orders where trunc(proc_dttm) = trunc(sysdate -3) group by to_char(proc_dttm , 'MONTH DD,YYYY')
)

SELECT "ID", "NAME", "BLOCK_SIZE", "RESIZE_STATE", "CURRENT_SIZE", "BUFFERS", "TARGET_SIZE", "TARGET_BUFFERS", "PREV_SIZE", "PREV_BUFFERS", "LO_BNUM", "HI_BNUM", "LO_SETID", "HI_SETID", "SET_COUNT" FROM(
select * from v$buffer_pool
)

SELECT "HIGHEST_OPEN_CUR", "MAX_OPEN_CUR" FROM(
SELECT  max(a.value) as highest_open_cur, p.value as max_open_cur FROM v$sesstat a, v$statname b, v$parameter p WHERE  a.statistic# = b.statistic# and b.name = 'opened cursors current' and p.name= 'open_cursors' group by p.value
)

SELECT "HIGHEST_OPEN_CUR", "MAX_OPEN_CUR" FROM(
SELECT  max(a.value) as highest_open_cur, p.value as max_open_cur FROM v$sesstat a, v$statname b, v$parameter p WHERE  a.statistic# = b.statistic# and b.name = 'opened cursors current' and p.name= 'open_cursors' group by p.value
)

SELECT "SID", "SERIAL#", "OPNAME", "TARGET", "TARGET_DESC", "SOFAR", "TOTALWORK", "UNITS", "START_TIME", "LAST_UPDATE_TIME", "TIMESTAMP", "TIME_REMAINING", "ELAPSED_SECONDS", "CONTEXT", "MESSAGE", "USERNAME", "SQL_ADDRESS", "SQL_HASH_VALUE", "SQL_ID", "SQL_PLAN_HASH_VALUE", "SQL_EXEC_START", "SQL_EXEC_ID", "SQL_PLAN_LINE_ID", "SQL_PLAN_OPERATION", "SQL_PLAN_OPTIONS", "QCSID", "CON_ID" FROM(
select * from v$session_longops
)

SELECT "OBJECT_NAME", "OPERATION", "OPTIONS", "COUNT(1)" FROM( select * from (
select p.object_name , p.operation , p.options , count(1) from dba_hist_sql_plan p, dba_hist_sqlstat s where p.object_owner <> 'SYS' and p.operation like '%INDEX%' and p.sql_id = s.sql_id and p.object_name not in ( select index_name from all_indexes where tablespace_name like '%16%'
) group by p.object_name, p.operation, p.options order by count(1) desc ) where rownum < 25 )

SELECT "OWNER", "JOB_NAME", "COMMENTS" FROM(
SELECT owner, job_name, comments FROM dba_scheduler_jobs
)

SELECT "JOB_NAME", "STATE" FROM(
SELECT JOB_NAME, STATE FROM DBA_SCHEDULER_JOBS
)

SELECT "LOG_ID", "LOG_DATE", "OWNER", "JOB_NAME", "JOB_SUBNAME", "STATUS", "ERROR#", "REQ_START_DATE", "ACTUAL_START_DATE", "RUN_DURATION", "INSTANCE_ID", "SESSION_ID", "SLAVE_PID", "CPU_USED", "CREDENTIAL_OWNER", "CREDENTIAL_NAME", "DESTINATION_OWNER", "DESTINATION", "ADDITIONAL_INFO" FROM(
select * from ALL_SCHEDULER_JOB_RUN_DETAILS
)
